import React, { useState } from "react";
import beeIcon from "../images/bee.png";
import "../sass/Bee.scss";

export default function Bee(props) {
  console.log(props.initialValues);
  let [health, setHealth] = useState(props.initialValues.initialScore);
  let [attackValue, setAttackValue] = useState(
    props.initialValues.attackNumber
  );
  let [score, setScore] = useState();

  // console.log(beeValues.attackNumber);
  // function updateScore(attackValue) {
  //   setScore(health - attackValue);
  //   console.log(score);
  // }

  function HandleAttack(event) {
    event.preventDefault();
    if (props.name === "Workers") {
      console.log(`this is a working bee`);
    } else if (props.name === "Drones") {
      console.log(`this is a drone bee`);
    } else if (props.name === "The Queen") {
      console.log(`this is the queen bee`);
    }
  }

  function getRandomNumber(event) {
    event.preventDefault();
    setAttackValue(Math.trunc(Math.random() * 100) + 1);
    console.log(attackValue);
  }

  return (
    <div className='Bee'>
      <h1 className='Bee__title'>{props.name}</h1>
      <div className='Bee__img-wrapper'>
        <img src={beeIcon} alt='little bee' className='Bee__img-logo' />
      </div>
      <h1 className='Bee__header'>‚ù§Ô∏è {health}%</h1>
      <h2 className='Bee__subheader'>üé≤</h2>
      <ul>
        <li>
          {props.name} need their health to stay above {props.minHealth}%!
        </li>
        <li>Alive status: {`isAlive`}</li>
      </ul>
      <button onClick={HandleAttack}>Attack</button>
      <button onClick={getRandomNumber}>Roll Dice</button>

      <button>Reset</button>
    </div>
  );
}
