  const [beeValues, setBeeValues] = useState({ props });
  console.log(beeValues);
  function updateScore(newScore) {}

  function HandleAttack(event) {
    event.preventDefault();
    updateScore();
    console.log(props.initialValues.attackNumber);
    console.log(props.initialValues.initialScore);
  }

  return (
    <div className='Bee'>
      <h1 className='Bee__title'>{beeValues.props.name}</h1>
      <div className='Bee__img-wrapper'>
        <img
          src={beeValues.props.initialValues.image}
          alt='little bee'
          className='Bee__img-logo'
        />
      </div>
      <h1 className='Bee__header'>health %</h1>
