import React, { useState } from "react";
import "../sass/Bee.scss";

export default function Bee(props) {
  const [health, setHealth] = useState(100);
  const [attackNumber, setAttackNumber] = useState(0);

  if (health <= 0) {
    console.log("you won!");
  }

  function Reset(event) {
    event.preventDefault();
    setHealth(100);
    setAttackNumber("");
  }

  function Attack(event) {
    event.preventDefault();
    setAttackNumber(Math.trunc(Math.random() * 100) + 1);
    let newScore = health - attackNumber;
    newScore <= 0 ? setHealth(0) : setHealth(newScore);

    return newScore;
  }
  console.log(newScore);
  return (
    <div className='worker'>
      <img src={props.image} alt='little bee' className='logo' />
      <h1>health: {health} %</h1>
      <h2>Attack power: {attackNumber} </h2>
      <ul>
        <li>
          {props.name} need their health to stay above {props.minHealth}%!
        </li>
        <li>Alive status: {}</li>
      </ul>
      <button onClick={Attack}>Attack!</button>
      <button onClick={Reset}>Reset</button>
    </div>
  );
}
