import "./sass/App.scss";
import Bees from "./components/Bee";
import beeImg from "./images/bee.png";
import { useEffect, useState } from "react";

function App() {
  const [loaded, setLoaded] = useState(false);
  const [beeInfo, setbeeInfo] = useState({});

  useEffect(() => {
    setLoaded(false);
  }, [beeInfo]);

  setbeeInfo({
    health: 100,
    initialAttack: Math.trunc(Math.random() * 100) + 1,
  });

  return (
    <div className='App'>
      <Bees image={beeImg} name='Workers' beeData={beeInfo} />

      <Bees image={beeImg} name='Drones' minHealth={50} />
      <Bees image={beeImg} name='The Queen' minHealth={20} />
    </div>
  );
}

export default App;
