import React, { useState } from "react";
import "../sass/Bee.scss";

export default function Bee(props) {
  const [health, setHealth] = useState(100);
  const [isAlive, setAlive] = useState("Alive");
  const [attackPower, setAttackPower] = useState(0);
  let aliveMsg = isAlive;

  function Damage(event) {
    event.preventDefault();
    let secretNumber = Math.trunc(Math.random() * 100) + 1;
    setAttackPower(secretNumber);
    let newScore = health - secretNumber;
    console.log(newScore);
    if (props.name === "Workers") {
      if (newScore >= 70) {
        setHealth(newScore);
      } else if (newScore < 70) {
        setHealth(newScore);
        setAlive("Dead");
      }
    }
  }

  return (
    <div className='worker'>
      <img src={props.image} alt='little bee image' className='logo' />
      <h1>health {health}%</h1>
      <h2>Attack power:{attackPower}%</h2>
      <ul>
        <li>
          {props.name} need their health to stay above {props.minHealth}%!
        </li>
        <li>{aliveMsg}</li>
      </ul>
      <button onClick={Damage}>Damage</button>
    </div>
  );
}
